//import logo from './logo.svg';
import React, { Component } from 'react';
import './App.css';
import Login from './Components/Login/Login';
import ForgotPassword from './Components/ForgotPassword/Forgotpassword';
import SignUp from './Components/SignUp/SignUp';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentView: 'login', // Default view
    };
  }

  // Method to change the current view
  changeView = (view) => {
    this.setState({ currentView: view });
  };

  // Action handlers
  handleLogin = (username, password) => {
    console.log(`Logging in with username: ${username}, password: ${password}`);
    // Perform login action (e.g., API call)
  };

  handleForgotPassword = (email) => {
    console.log(`Resetting password for email: ${email}`);
    // Perform forgot password action (e.g., API call)
  };

  handleSignUp = (username, email, password) => {
    console.log(`Signing up with username: ${username}, email: ${email}, password: ${password}`);
    // Perform sign-up action (e.g., API call)
  };

  handleRegister = (fullName, email, password) => {
    console.log(`Registering with fullName: ${fullName}, email: ${email}, password: ${password}`);
    // Perform register action (e.g., API call)
  };

  render() {
    const { currentView } = this.state;

    return (
      <div className="app">
        {currentView === 'login' && (
          <Login
            onLogin={this.handleLogin}
            onForgotPassword={() => this.changeView('forgotPassword')}
            onSignUp={() => this.changeView('signup')}
          />
        )}
        {currentView === 'forgotPassword' && (
          <ForgotPassword
            onForgotPassword={this.handleForgotPassword}
            onBackToLogin={() => this.changeView('login')}
          />
        )}
        {currentView === 'signup' && (
          <SignUp
            onSignUp={this.handleSignUp}
            onBackToLogin={() => this.changeView('login')}
          />
        )}
        {currentView === 'register' && (
          <Register
            onRegister={this.handleRegister}
            onBackToLogin={() => this.changeView('login')}
          />
        )}
      </div>
    );
  }
}

export default App;
